<?phpdefined('COT_CODE') or die('Wrong URL.');/** * Excel Page Importer / Exporter plugin * * @package excelpagemanager * @version 2.0.0 * @author esclkm * @copyright Copyright (c) esclkm 2008-2013 * @license BSD *//** * Filters importing data * * @param string $value filter value * @param string $type filter type * @param string $arg параметр фильтра * @param string $default значение по умолчанию * @return string */function filterxlsx($value, $type='string', $arg='', $default=''){	switch ($type)	{		case 'static':			$sval = $arg;			break;		case 'double':			$sval = (double)$value;			break;		case 'int':			$sval = (int)$value;			break;		case 'bool':			$sval = ($value == $arg) ? 1 : 0;			break;		case 'array':		/*	cot_print($arg, $value, $default, $arg[$value]);*/			$sval = $arg ? $arg[$value] : $default;			break;		case 'callback':			if (function_exists($arg))			{				$sval = $arg($value, $default);			}			break;		case 'string':		default:			if (empty($value) && !empty($arg))			{				$value = $arg;			}			$sval = $value;			break;	}		return $sval;}